#Function to create the whole image
def save_image(solution):#solution is a given list of positions of queens
    if directory == '':
        out_path = "C:\\Users\\Library\\Desktop\\image\\result\\"+'_'.join(map(lambda a: str(a),solution))+'.png'#path to save image file
    else:
        out_path = directory+"\\"+'_'.join(map(lambda a: str(a),solution))+'.png'#path to save file
    #creating blank chess box
    #Co-ordinates of the boxes are given as (uppen left corner x pos , uppen left corner y pos ) of the image as a tuple 
    pointx = 0
    pointy = 0
    black = True
    for j in range(n):#increment for row
        if j%2 == 0:#for changing the black <---> white in each row
            black = True
        else:
            black = False
        for i in range(n):#increment for boxes in a row
            if black:#determination of box colour 
                im.paste(bl,box=(pointx,pointy))#pasting the black/white box to the pointer --> ( pointx , pointy )
                pointx += box_len_pix #new pointer location for next box
                black = False
            else:
                im.paste(wh,box=(pointx,pointy))#pasting the black/white box to the pointer --> ( pointx , pointy )
                pointx += box_len_pix #new pointer location for next box
                black = True
        #redefining pointers for new row
        pointx = 0
        pointy = pointy+box_len_pix

    #appending the solution position with appropiate images in blank chess box
    for pos in solution:
        box = (list(solution).index(pos)*box_len_pix,pos*box_len_pix)#position of the solution box
        if im.getpixel((box[0]+(box_len_pix/2),box[1]+(box_len_pix/2))) == (255, 255, 255,255):#Checking the current box colour upon which the new image will be pasted
            im.paste(wh_king,box)#appending the solution position with appropiate images (if white)
        else:
            im.paste(bl_king,box)#appending the solution position with appropiate images with opposite colour (if black)
    
    im.save(out_path)#saving the resultent image in the given path

